import React from "react";
import { View, ScrollView, StyleSheet, TouchableOpacity } from "react-native";
import { Screen, Container, Text } from "../../components/ui";
import { Icon } from "react-native-elements";
import {
  Ionicons,
  MaterialIcons,
  FontAwesome5,
  MaterialCommunityIcons,
} from "@expo/vector-icons";

import { useDarkMode } from "../../providers/DarkModeProvider";
import MenuItem from "./Item_menu/MenuItem";
import { darkTheme, lightTheme } from "../../theme";

export default function ({ navigation }) {
  const { darkMode, setDarkMode } = useDarkMode();

  return (
    <View
      style={[
        styles.container,
        { backgroundColor: darkMode ? darkTheme.body : lightTheme.body },
      ]}
    >
      <View style={styles.start}>
        <View style={styles.square}>
          <Text
            style={[
              styles.menu,
              {
                borderColor: darkMode
                  ? darkTheme.borderColor
                  : lightTheme.borderColor,
                color: darkMode ? darkTheme.text : lightTheme.text,
              },
            ]}
          >
            T E L A S
          </Text>
        </View>

        <View style={styles.menuContainer}>
          <View style={styles.menuItem}>
            <TouchableOpacity
              onPress={() =>
                navigation.navigate("Main", { screen: "ClientesScreen" })
              }
            >
              <View
                style={[
                  styles.image,
                  {
                    borderColor: darkMode
                      ? darkTheme.borderColor
                      : lightTheme.borderColor,
                  },
                ]}
              >
                <MaterialCommunityIcons
                  name="account-search"
                  size={40}
                  color={darkMode ? darkTheme.icon : lightTheme.icon}
                />
                <View style={styles.txt}>
                  <Text>Consulta</Text>
                  <Text style={{ marginLeft: "8%", bottom: "6%" }}>
                    cliente
                  </Text>
                </View>
              </View>
            </TouchableOpacity>
          </View>
          <View style={styles.menuItem}>
            <TouchableOpacity
              onPress={() => {
                navigation.navigate("Main", { screen: "Consulta_produto" });
              }}
            >
              <View
                style={[
                  styles.image,
                  {
                    borderColor: darkMode
                      ? darkTheme.borderColor
                      : lightTheme.borderColor,
                  },
                ]}
              >
                <MaterialCommunityIcons
                  name="package-variant"
                  size={40}
                  color={darkMode ? darkTheme.icon : lightTheme.icon}
                />
                <View style={styles.txt}>
                  <Text>Consulta</Text>
                  <Text style={{ bottom: "6%" }}>produtos</Text>
                </View>
              </View>
            </TouchableOpacity>
          </View>
          <View style={styles.menuItem}>
            <TouchableOpacity
              onPress={() => {
                navigation.navigate("Main", { screen: "Consulta_pedidos" });
              }}
            >
              <View
                style={[
                  styles.image,
                  {
                    borderColor: darkMode
                      ? darkTheme.borderColor
                      : lightTheme.borderColor,
                  },
                ]}
              >
                <FontAwesome5
                  name="clipboard-list"
                  size={40}
                  color={darkMode ? darkTheme.icon : lightTheme.icon}
                />
                <View style={styles.txt}>
                  <Text>Consulta</Text>
                  <Text style={{ marginLeft: "3%", bottom: "6%" }}>
                    pedidos
                  </Text>
                </View>
              </View>
            </TouchableOpacity>
          </View>
          <View style={styles.menuItem}>
            <TouchableOpacity
              onPress={() => {
                navigation.navigate("Main", { screen: "Consulta_contatos" });
              }}
            >
              <View
                style={[
                  styles.image,
                  {
                    borderColor: darkMode
                      ? darkTheme.borderColor
                      : lightTheme.borderColor,
                  },
                ]}
              >
                <MaterialCommunityIcons
                  name="phone-log"
