import React, { useEffect, useRef, useState } from "react";
import PropTypes from "prop-types";
import {
  View,
  FlatList,
  VirtualizedList,
  ScrollView,
  TouchableOpacity,
  Vibration,
  Picker,
  StyleSheet,
  Text,
  Platform,
  TextInput,
  CheckBox,
  Dimensions,
  Image,
  Modal,
  TouchableHighlight,
} from "react-native";
import { StatusBar } from "../../components/StatusBar";
import RNPickerSelect from "react-native-picker-select";
import { Checkbox } from "react-native-paper";
import { useDarkMode } from "../../providers/DarkModeProvider";
import { darkTheme, lightTheme } from "../../theme";

import {
  Container,
  Button,
  Title,
  StyledView,
  Card,
  Fab,
  BottomSheetModal,
  ListItem,
} from "../../components/ui";
import { Input } from "react-native-elements";
import { Icon } from "react-native-elements";
import {
  MaterialIcons,
  FontAwesome5,
  MaterialCommunityIcons,
} from "@expo/vector-icons";

import SectionList, {
  ListItemContainer,
} from "../../components/SectionList/SectionList";
import Header from "../../components/Header/Header";

import { Ionicons } from "@expo/vector-icons";

//import { LargeList } from "react-native-largelist";

const { width, height } = Dimensions.get("screen");

export default function ClientesScreen({ navigation, route }) {
  const { darkMode } = useDarkMode();
  const [selectedValue, setSelectedValue] = useState("Select a course");
  const [checked, setChecked] = useState(false);
  const [checked_two, setChecked_two] = useState(false);
  const [shouldShow, setShouldShow] = useState(true);
  const [shouldShowTwo, setShouldShowTwo] = useState(true);
  const [modalVisible, setModalVisible] = useState(false);

  const icone = (_) => {
    setChecked(!checked);
  };

  const icone_two = (_) => {
    setChecked_two(!checked_two);
  };

  return (
    <Container full>
      <Header
        navigation={navigation}
        title="Adicionar Clientes"
        transparent
        style={{ margin: -20, marginBottom: 20 }}
      />
      <ScrollView
        showsVerticalScrollIndicator={false}
        style={
          Platform.OS === "ios"
            ? pickerSelectStyles.inputIOS
            : pickerSelectStyles.inputAndroid
        }
        style={{ flex: 1 }}
      >
        <View
          style={[
            styles.divider,
            {
              borderBottomColor: darkMode
                ? darkTheme.borderColor
                : lightTheme.borderColor,
            },
          ]}
        >
          <Text
            style={[
              styles.basicas,
              {
                color: darkMode ? darkTheme.text : lightTheme.text,
              },
            ]}
          >
            Informações Básicas
          </Text>
        </View>

        <View style={styles.pic}>
          <RNPickerSelect
            placeholder={{
              label: "Escolha",
              value: null,
              color: "red",
            }}
            onValueChange={(value) => console.log(value)}
            items={[
              { label: "Pessoa Juridica", value: "Pessoa_juridica" },
              { label: "Pessoa Física", value: "Pessoa_física" },
            ]}
            style={pickerSelectStyles}
            useNativeAndroidPickerStyle={false}
          />
        </View>

        <View>
          <View>
            <TextInput
              placeholder="*Nome Completo"
              placeholderTextColor="#000"
              style={styles.inp}
            />
          </View>

          <View>
            <TextInput
              placeholder="Registro de Identidade"
              placeholderTextColor="#000"
              keyboardType="number-pad"
              style={styles.inp}
            />
          </View>

          <TextInput
            placeholder="*CPF"
            placeholderTextColor="#000"
            keyboardType="number-pad"
            style={styles.inp}
          />
          <TextInput
            placeholder="Email"
            placeholderTextColor="#000"
            style={styles.inp}
          />
          <TextInput
            placeholder="Data de aniversário"
            placeholderTextColor="#000"
            style={styles.inp}
          />
        </View>
        <View
          style={[
            styles.divider,
            {
              borderBottomColor: darkMode
                ? darkTheme.borderColor
                : lightTheme.borderColor,
            },
          ]}
        >
          <Text
            style={[
              styles.basicas,
              {
                color: darkMode ? darkTheme.text : lightTheme.text,
              },
            ]}
          >
            Endereço
          </Text>
        </View>
        <View style={{ paddingTop: "5%" }}>
          <TextInput
            placeholder="*CEP"
            placeholderTextColor="#000"
            keyboardType="number-pad"
            style={styles.inp}
          />
          <TextInput
            placeholder="Endereço"
            placeholderTextColor="#000"
            style={styles.inp}
          />
          <TextInput
            placeholder="Complemento"
            placeholderTextColor="#000"
            style={styles.inp}
          />
          <View style={{ marginTop: "-6%", paddingBottom: "6%" }}>
            <RNPickerSelect
              onValueChange={(value) => console.log(value)}
              items={[
                { label: "Football", value: "football" },
                { label: "Baseball", value: "baseball" },
                { label: "Hockey", value: "hockey" },
              ]}
              style={pickerSelectStyles}
              useNativeAndroidPickerStyle={false}
            />
          </View>

          <TextInput
            placeholder="*Cidade"
            placeholderTextColor="#000"
            style={styles.inp}
          />
          <TextInput
            placeholder="*Bairro"
            placeholderTextColor="#000"
            style={styles.inp}
          />
          <TextInput
            placeholder="Telefone"
            placeholderTextColor="#000"
            keyboardType="number-pad"
            style={styles.inp}
          />
          <TextInput
            placeholder="Telefone 2"
            placeholderTextColor="#000"
            keyboardType="number-pad"
            style={styles.inp}
          />
          <View style={styles.textAreaContainer}>
            <TextInput
              style={styles.textArea}
              underlineColorAndroid="transparent"
              placeholder="Observação"
              placeholderTextColor="#000"
              numberOfLines={10}
              multiline={true}
            />
          </View>
          <View style={styles.checkboxContainer}>
            <TouchableOpacity
              onPress={() => icone()}
              onPress={() => setShouldShow(!shouldShow)}
              style={
                Platform.OS === "ios"
                  ? checkBoxStyles.inputIOS
                  : checkBoxStyles.inputAndroid
              }
            >
              <MaterialCommunityIcons
                name={checked ? "check" : "checkbox-blank-circle-outline"}
                size={30}
                color="#fff"
              />
            </TouchableOpacity>
            <View
              style={{
                justifyContent: "space-between",
                alignItems: "center",
                flexDirection: "row",
                flex: 1,
              }}
            >
              <Text
                style={
                  Platform.OS === "ios"
                    ? txtStyles.inputIOS
                    : txtStyles.inputAndroid
                }
              >
                End.entrega
              </Text>
              <Text
                style={
                  Platform.OS === "ios"
                    ? txtStyles.inputIOS
                    : txtStyles.inputAndroid
                }
              >
                End.cobrança
              </Text>
            </View>
            <TouchableOpacity
              onPress={() => icone_two()}
              onPress={() => setShouldShowTwo(!shouldShowTwo)}
              style={
                Platform.OS === "ios"
                  ? checkBoxStyles.inputIOS
                  : checkBoxStyles.inputAndroid
              }
            >
              <MaterialCommunityIcons
                name={checked_two ? "check" : "checkbox-blank-circle-outline"}
                size={30}
                color="#fff"
              />
            </TouchableOpacity>
          </View>
        </View>
        {/* End.entrega */}
