import React from "react";
import PropTypes from "prop-types";
import {
  View,
  Image,
  Dimensions,
  ScrollView,
  FlatList,
  VirtualizedList,
  BackHandler,
} from "react-native";
import { StatusBar } from "../../components/StatusBar";
import {
  Container,
  Button,
  Title,
  StyledView,
  Input,
  Text,
  ButtonCard,
} from "../../components/ui";
import Carousel from "react-native-carousel-control";

// Assets
import { imgs } from "../../components/assets";

const { width, height } = Dimensions.get("screen");

export default function InicioScreen({ navigation }) {
  //Sair ao chegar na tela inicial
  BackHandler.addEventListener("hardwareBackPress", (e) => {
    if (navigation.canGoBack() === false) {
      alert("Sair?");
      return true;
    }
    return false;
  });

  return (
    <Container>
      <ScrollView showsVerticalScrollIndicator={false} style={{ flex: 1 }}>
        <View style={{ height: 50, alignItems: "center", marginBottom: 30 }}>
          <Image
            source={imgs.logoWSGE_cinza}
            style={{
              maxWidth: 150,
              maxHeight: 49,
              resizeMode: "stretch",
            }}
          />
        </View>

        <View>
          <StyledView style={{ height: Dimensions.get("window").width }}>
            {/*<Text>Carrosseu de Gráficos</Text>*/}
            <Carousel
              pageStyle={{
                backgroundColor: "white",
                borderRadius: 5,
                right: 35,
                justifyContent: "center",
                alignItems: "center",
              }}
              swipeThreshold={0.1}
            >
              <View
                style={{
                  //borderWidth: 3,
                  width: width / 1.3,
                  height: height / 2.1,
                  marginTop: "3%",
                }}
              >
                <View
                  style={{
                    flexDirection: "column",
                    justifyContent: "center",
                    alignItems: "center",
                  }}
                >
                  <Text style={{ color: "#000", fontSize: 18 }}>
                    HISTÓRICO DE PEDIDOS
                  </Text>
                  <Text style={{ color: "#000", fontSize: 15 }}>
                    Outubro 2020
                  </Text>
                </View>
                <View
                  style={{
                    justifyContent: "center",
                    alignItems: "center",
                    marginTop: "19%",
                  }}
                >
                  <Text style={{ color: "#000", fontSize: 27 }}>
                    NÃO HÁ REGISTROS
                  </Text>
                  <Text style={{ color: "#000", fontSize: 27 }}>
                    PARA ESTE MÊS!
                  </Text>
                </View>
                <View
                  style={{
                    flexDirection: "column",
                    justifyContent: "flex-end",
                    alignItems: "flex-start",
                    height: height / 4.5,
                    //borderWidth: 2,
                  }}
                >
                  <View
                    style={{
                      flexDirection: "row",
                      justifyContent: "center",
                      alignItems: "center",
                    }}
                  >
                    <View
                      style={{
                        backgroundColor: "#2ecc71",
                        width: "4%",
                        height: height / 60,
                      }}
                    />
                    <View style={{ marginLeft: "3%" }}>
                      <Text style={{ color: "#000", fontSize: 14 }}>
                        Pedido
                      </Text>
                    </View>
                  </View>
                  <View
                    style={{
                      flexDirection: "row",
                      //borderWidth: 2,
                      justifyContent: "center",
                      alignItems: "center",
                    }}
                  >
                    <View
                      style={{
                        backgroundColor: "#d63031",
                        width: "4%",
                        height: height / 60,
                      }}
                    />
                    <View style={{ marginLeft: "3%" }}>
                      <Text style={{ color: "#000", fontSize: 14 }}>
                        Orçamento
                      </Text>
                    </View>
                  </View>
                  <View
                    style={{
                      flexDirection: "row",
                      //borderWidth: 2,
                      justifyContent: "center",
                      alignItems: "center",
                    }}
                  >
                    <View
                      style={{
                        backgroundColor: "#0652DD",
                        width: "4%",
                        height: height / 60,
                      }}
                    />
                    <View style={{ marginLeft: "3%" }}>
                      <Text style={{ color: "#000", fontSize: 14 }}>
                        Farturado
                      </Text>
                    </View>
                  </View>
                  <View
                    style={{
                      flexDirection: "row",
                      //borderWidth: 2,
                      justifyContent: "center",
                      alignItems: "center",
                    }}
                  >
                    <View
                      style={{
                        backgroundColor: "#d35400",
                        width: "4%",
                        height: height / 60,
                      }}
                    />
                    <View style={{ marginLeft: "3%" }}>
                      <Text style={{ color: "#000", fontSize: 14 }}>
                        Gerada venda
                      </Text>
                    </View>
                  </View>
                  <View
                    style={{
                      flexDirection: "row",
                      //borderWidth: 2,
                      justifyContent: "center",
                      alignItems: "center",
                    }}
                  >
                    <View
                      style={{
                        backgroundColor: "#181818",
                        width: "4%",
                        height: height / 60,
                      }}
                    />
                    <View style={{ marginLeft: "3%" }}>
                      <Text style={{ color: "#000", fontSize: 14 }}>
                        Cancelado
                      </Text>
                    </View>
                  </View>
                </View>
              </View>
              <View>
                <View
                  style={{
                    //borderWidth: 3,
                    width: width / 1.3,
                    height: height / 2.1,
                    marginTop: "3%",
                  }}
                >
                  <View
                    style={{
                      flexDirection: "column",
                      justifyContent: "center",
                      alignItems: "center",
                    }}
                  >
                    <Text style={{ color: "#000", fontSize: 18 }}>
                      TOP 3 PRODUTOS EM VENDAS
                    </Text>
                    <Text style={{ color: "#000", fontSize: 15 }}>
                      Outubro 2020
                    </Text>
                  </View>
                  <View
                    style={{
                      justifyContent: "center",
                      alignItems: "center",
                      marginTop: "19%",
                    }}
                  >
                    <Text style={{ color: "#000", fontSize: 27 }}>
                      NÃO HÁ REGISTROS
                    </Text>
                    <Text style={{ color: "#000", fontSize: 27 }}>
                      PARA ESTE MÊS!
                    </Text>
                  </View>
                </View>
              </View>
              <View>
                <View
                  style={{
                    //borderWidth: 3,
                    width: width / 1.3,
                    height: height / 2.1,
                    marginTop: "3%",
                  }}
                >
                  <View
                    style={{
                      flexDirection: "column",
                      justifyContent: "center",
                      alignItems: "center",
                    }}
                  >
                    <Text style={{ color: "#000", fontSize: 18 }}>
                      TOP 3 CLIENTES EM VENDAS
                    </Text>
                    <Text style={{ color: "#000", fontSize: 15 }}>
                      Outubro 2020
                    </Text>
                  </View>
