import React, { useEffect, useRef, useState } from "react";
import PropTypes from "prop-types";
import {
  View,
  FlatList,
  VirtualizedList,
  ScrollView,
  TouchableOpacity,
  Vibration,
  StyleSheet,
  Image,
  Alert,
  TextInput,
} from "react-native";
import { StatusBar } from "../../components/StatusBar";
import { FontAwesome5, MaterialCommunityIcons } from "@expo/vector-icons";

import {
  Container,
  Button,
  Text,
  Title,
  StyledView,
  Input,
  Card,
  CardPedido,
  Fab,
  BottomSheetModal,
  ListItem,
} from "../../components/ui";

import SectionList, {
  ListItemContainer,
} from "../../components/SectionList/SectionListPedidos";
import Header from "../../components/Header/Header";

import { Ionicons } from "@expo/vector-icons";
import styles from "react-native-carousel-control/styles";

//import { LargeList } from "react-native-largelist";

export default function ClientesScreen({ navigation, route }) {
  const sectionRef = useRef();
  const [totalClientes, setTotalClientes] = useState(0);
  const [listaClientes, setListaClientes] = useState([
    {
      title: "#",
      data: [
        {
          num: "302",
          empresa: "100 POR CENTO DROGARIA",
          end: "A.DE MARTINO DROGARIA DE QUEIMADOS",
        },
        {
          num: "302",
          empresa: "100 POR CENTO DROGARIA",
          end: "A.DE MARTINO DROGARIA DE QUEIMADOS",
        },
        {
          num: "302",
          empresa: "100 POR CENTO DROGARIA",
          end: "A.DE MARTINO DROGARIA DE QUEIMADOS",
        },
        {
