import React, { useEffect, useRef, useState } from "react";
import PropTypes from "prop-types";
import {
  View,
  FlatList,
  VirtualizedList,
  ScrollView,
  TouchableOpacity,
  Vibration,
  Picker,
  StyleSheet,
  Text,
  Platform,
  TextInput,
  CheckBox,
  Dimensions,
} from "react-native";
import { StatusBar } from "../../components/StatusBar";
import RNPickerSelect from "react-native-picker-select";
import { useDarkMode } from "../../providers/DarkModeProvider";
import { darkTheme, lightTheme } from "../../theme";

import {
  Container,
  Button,
  Title,
  StyledView,
  Card,
  Fab,
  BottomSheetModal,
  ListItem,
} from "../../components/ui";
import { Input } from "react-native-elements";
import { Icon } from "react-native-elements";
import {
  MaterialIcons,
  FontAwesome5,
  MaterialCommunityIcons,
} from "@expo/vector-icons";

import SectionList, {
  ListItemContainer,
} from "../../components/SectionList/SectionList";
import Header from "../../components/Header/Header";

import { Ionicons } from "@expo/vector-icons";

const { width, height } = Dimensions.get("screen");

//import { LargeList } from "react-native-largelist";

export default function ClientesScreen({ navigation, route }) {
  const [selectedValue, setSelectedValue] = useState("Select a course");
  const [isSelected, setSelection] = useState(false);
  const { darkMode } = useDarkMode();

  return (
    <Container full>
      <Header
        navigation={navigation}
        title="Cadastro de contato"
        transparent
        style={{ margin: -20, marginBottom: 20 }}
      />
      <ScrollView
        showsVerticalScrollIndicator={false}
        style={
          Platform.OS === "ios"
            ? pickerSelectStyles.inputIOS
            : pickerSelectStyles.inputAndroid
        }
        style={{ flex: 1, marginTop: "-7%" }}
      >
        <View
          style={[
            styles.divider,
            {
              borderColor: darkMode
                ? darkTheme.borderColor
                : lightTheme.borderColor,
            },
          ]}
        >
          <Text
            style={[
              styles.basicas,
              { color: darkMode ? darkTheme.text : lightTheme.text },
            ]}
          >
            Informações Básicas
          </Text>
        </View>
        <View style={{ marginTop: "5%" }} />
        <View style={styles.pic}>
          <RNPickerSelect
            placeholder={{
              label: "Selecione o tipo de contato",
              value: null,
              color: "#000",
            }}
            onValueChange={(value) => console.log(value)}
            items={[
              { label: "Cliente", value: "cliente" },
              { label: "Outro", value: "outro" },
            ]}
            style={pickerSelectStyles}
            useNativeAndroidPickerStyle={false}
          />
        </View>

        <View>
          <View>
            <TextInput
              placeholder="Nome do contato"
              placeholderTextColor="#555"
              autoCompleteType="name"
              style={styles.inp}
            />
          </View>

          <View>
            <TextInput
              placeholder="Número de documento"
              placeholderTextColor="#555"
              keyboardType="number-pad"
              style={styles.inp}
            />
          </View>

          <TextInput
            placeholder="Setor"
            placeholderTextColor="#555"
            style={styles.inp}
          />
          <TextInput
            placeholder="Data Aniversário/Inauguração"
            placeholderTextColor="#555"
            style={styles.inp}
          />

          <View style={styles.cargo}>
            <RNPickerSelect
              placeholder={{
                label: "Selecione o cargo",
                value: null,
                color: "#555",
              }}
              onValueChange={(value) => console.log(value)}
              items={[
                { label: "Teste 1", value: "Teste_1" },
                { label: "Teste 2", value: "Teste_2" },
              ]}
              style={pickerSelectStylesCargo}
              useNativeAndroidPickerStyle={false}
            />
            <TouchableOpacity style={styles.btn_two}>
              <MaterialCommunityIcons name="plus" size={40} color="#fff" />
            </TouchableOpacity>
          </View>
          <TextInput
            placeholder="Email"
            placeholderTextColor="#555"
            keyboardType="email-address"
            autoCompleteType="email"
            style={styles.inp}
          />
        </View>
        <View>
          <View
            style={[
              styles.divider,
              {
                borderColor: darkMode
                  ? darkTheme.borderColor
                  : lightTheme.borderColor,
              },
            ]}
          >
            <Text
              style={[
                styles.basicas,
                { color: darkMode ? darkTheme.text : lightTheme.text },
              ]}
            >
              Endereço
            </Text>
          </View>
          <View style={{ marginTop: "5%" }}>
            <View style={{ flexDirection: "row" }}>
              <TextInput
                placeholder="CEP"
                placeholderTextColor="#555"
                keyboardType="number-pad"
                style={styles.cep}
              />
              <View style={{ flexDirection: "row" }}>
                <TouchableOpacity style={styles.btn}>
                  <Text
                    numberOfLines={1}
                    adjustsFontSizeToFit
                    style={styles.btnTxt}
                  >
                    Consulta CEP
                  </Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.btn}>
                  <Text
                    numberOfLines={1}
                    adjustsFontSizeToFit
                    style={styles.btnTxt}
                  >
                    Esqueci o CEP
                  </Text>
                </TouchableOpacity>
              </View>
            </View>
            <TextInput
              placeholder="Endereço"
              placeholderTextColor="#555"
              style={styles.inp}
            />
            <TextInput
              placeholder="Número"
              placeholderTextColor="#555"
              keyboardType="number-pad"
              style={styles.inp}
            />
            <TextInput
              placeholder="Complemento"
              placeholderTextColor="#555"
              style={styles.inp}
            />
            <View style={styles.pic}>
              <RNPickerSelect
                placeholder={{
                  label: "Selecione um estado",
                  value: null,
                  color: "#555",
                }}
